bool comment_analyzer::is_arial( wchar_t charactor )
{
	return ( charactor >= 0x0000 && charactor <= 0x007f )
		|| ( charactor >= 0x00a0 && charactor <= 0x0107 )
		|| ( charactor >= 0x040e && charactor <= 0x044f )
		|| arial_set_.find( charactor ) != arial_set_.cend();
}

bool comment_analyzer::is_ms_p_gothic( wchar_t charactor )
{
	return ( charactor >= 0xe000 && charactor <= 0xe757 )
		|| ms_p_gothic_set_.find( charactor ) != ms_p_gothic_set_.cend();
}

bool comment_analyzer::is_sim_sun( wchar_t charactor )
{
	return sim_sun_set_.find( charactor ) != sim_sun_set_.cend();
}

bool comment_analyzer::is_gulim( wchar_t charactor )
{
	return ( charactor >= 0xac00 && charactor <= 0xd7a3 )
		|| gulim_set_.find( charactor ) != gulim_set_.cend();
}

bool comment_analyzer::is_p_ming_liu( wchar_t charactor )
{
	return charactor == 0x02cd
		|| charactor == 0x2027
		|| charactor == 0x2574;
}

void comment_analyzer::analysis( rendering_comment& comment )
{
	comment_analysis_data data;

	const auto begin = comment.value();
	const auto end = comment.value() + comment.length();

	auto font = static_cast<comment_font_type>( 0 );
	auto gBegin = begin;
	for( auto p = begin; p != end; ++p )
	{
		// cp1250-1258
		if( is_arial( *p ) )
		{
			if( font != comment_font_type::arial )
			{
				if( gBegin != p )
				{
					data.emplace_back( gBegin, p - 1, font );
				}
				font = comment_font_type::arial;
				gBegin = p;
			}
		}
		// CJK Kanji (cp932, cp936, cp949, cp950)
		else
		{
			if( font != comment_font_type::ms_p_gothic )
			{
				if( gBegin != p )
				{
					data.emplace_back( gBegin, p - 1, font );
				}
				font = comment_font_type::ms_p_gothic;
				gBegin = p;
			}
		}
	}
	if( gBegin != end )
	{
		data.emplace_back( gBegin, end - 1, font );
	}

	auto first_group = true;
	auto base_font = static_cast<comment_font_type>( 0 );
	for( auto g = data.begin(); g != data.end(); ++g )
	{
		if( g->font != comment_font_type::ms_p_gothic )
		{
			continue;
		}

		bool change = false;
		for( auto c = g->begin; c != g->end; ++c )
		{
			if( is_ms_p_gothic( *c ) )
			{
				change = true;
				break;
			}
			else if( is_sim_sun( *c ) )
			{
				change = true;
				g->font = comment_font_type::sim_sun;
				break;
			}
			else if( is_gulim( *c ) )
			{
				change = true;
				g->font = comment_font_type::gulim;
				break;
			}
			else if( is_p_ming_liu( *c ) )
			{
				change = true;
				g->font = comment_font_type::p_ming_li_u;
				break;
			}
		}
		if( first_group )
		{
			base_font = g->font;
			first_group = false;
		}
		else if( !change )
		{
			g->font = base_font;
		}
	}

	comment.set_analysis_data( data );
}